SELECT cy.yhbh, cy.yhjb, cy.tcbl, cy.fzrs, (CASE WHEN cy.status=0 THEN 'VIP' else '普通用户' END) status ,
cy.ztms FROM csb_yh cy where 1=1
{? AND cy.yhbh = #yhbh#}
{? AND cy.status IN ($status$)} {? AND cy.yhjb = #yhjb#}

SELECT DL.dldxbh, DL.dljhm, DL.tgslj, (CASE WHEN statues='1' THEN '已激活' ELSE '未激活' END) statues , DL.notes1
FROM yhdx_dl DL left JOIN hbdx_user HBUSER ON HBUSER.dldxbhfk =DL.DLDXBH WHERE 1=1
{? AND DL.DLDXBH=#DLDXBH#} {? AND DL.dljhm=#DLJHM#} {? AND DL.STATUES=#STATUES#}

	   UPDATE yhdx_dl
                  SET statues = '1' where 1=1 
                  {? AND dljhm=#dljhm#}
                  {? AND DLDXBH=#DLDXBH#}
